import React from "react";
import axios from "axios";

export const getTaxonomyfromAPI = () => {
  let allTaxonomy = {
    data: [],
    status: "404",
  };
  axios
    .get(
      "http://localhost:8500/celerant360/server/coldfusion/taxonomy.cfc?method=getTaxonomy"
    )
    .then((response) => {
      console.log("response");
      console.log(response);
      allTaxonomy.data = response.data;
      allTaxonomy.status = response.status;
    })
    .catch((error) => {
      allTaxonomy.data = [];
      allTaxonomy.status = 404;
    });

  return allTaxonomy;
};
