import axios from "axios";
import { reactLocalStorage } from "reactjs-localstorage";
import React, { useState, useEffect } from "react";
import { useStateValue } from "./StateProvider";

export const GetTaxonomyfromAPI = async () => {
  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getTaxonomy";

  try {
    const response = await axios.get(url);
    if (!response.data.includes("table.cfdump_wddx")) {
      let data = response.data;
      reactLocalStorage.setObject("allTaxonomy", data);
    }
  } catch (error) {}
};

export const allProductsAPI = async () => {
  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getAllProducts";

  try {
    const response = await axios.get(url);
    if (!response.data.includes("table.cfdump_wddx")) {
      let data = response.data;
      reactLocalStorage.setObject("allProductsData", data);
    }
  } catch (error) {}
};

export const productsAPI = async () => {
  const [{ basket, sFilters }, dispatch] = useStateValue();

  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getAllProducts";

  try {
    const response = await axios.get(url);
    if (!response.data.includes("table.cfdump_wddx")) {
      let data = response.data;
      reactLocalStorage.setObject("allProductsData", data);
    }
  } catch (error) {}
};
