import React, { useState, useEffect } from "react";
import { useStateValue } from "../StateProvider";
import { useHistory } from "react-router-dom";
import { reactLocalStorage } from "reactjs-localstorage";
import { useParams } from "react-router";
import * as productController from "./product/productController";
import * as productView from "./product/productViews";
import * as mcss from "../css/magiczoompluscss.css";
import MagicZoom from "../js/magiczoomplusReactJs";
import { prodImagesDir } from "../settings";

function Product() {
  let cart = [];

  const [{ basket }, dispatch] = useStateValue();
  let sessions = [];
  sessions = reactLocalStorage.getObject("cartSessions");

  const params = useParams();
  let product = {};
  product = productController.getProduct(params.id);
  console.log(product);

  productController.setRecentlyViewed(product, reactLocalStorage);
  return (
    <>
      <section id="contentHolder">
        <div id="productpage">
          <div className="container">
            <div className="row">
              <BreadCrumbDisplay product={product} dispatch={dispatch} />
              <ProductImage product={product} />
              <ProductInfo product={product} />
              {/*<ProductNewsLetters product={product} />*/}
              <ProductDetails product={product} />
              <AdditionalProducts product={product} />
            </div>
          </div>
        </div>
      </section>
    </>
  );
}
const BreadCrumbDisplay = (props) => {
  let history = useHistory();

  let breadcrumbs;
  breadcrumbs = productController.getProductBreadCrumbs(
    props.product,
    history,
    props.dispatch
  );
  return productView.BreadCrumbDisplay(breadcrumbs);
};

const ProductImage = (props) => {
  let prodImageInfo = productController.getProductImage(props);

  return productView.displayProductImage(prodImageInfo);
};

const ProductInfo = (props) => {
  let prodInfo = productController.getProductInfo(props);
  return productView.displayProductInfo(prodInfo);
};

const ProductNewsLetters = (props) => {
  let prodNewsLetters = productController.getProductNewsLetters(props);
  return productView.displayProductNewsLetters(prodNewsLetters);
};

const ProductDetails = (props) => {
  let prodNewsLetters = productController.getProductDetails(props);
  return productView.displayProductDetails(prodNewsLetters);
};

const AdditionalProducts = (props) => {
  document.getElementById("zoom-image").src =
    prodImagesDir + props.product.largeImage;

  document.getElementById("zoomer").src =
    prodImagesDir + props.product.largeImage;
  try {
    document.getElementById("mz-lens").src =
      prodImagesDir + props.product.largeImage;
  } catch (err) {}

  MagicZoom.refresh();

  let additionalProducts = productController.getAdditionalProducts(props);
  return productView.displayAdditionalProducts(additionalProducts);
};

export default Product;
