const RefineSearch = (e, setSearchText) => {
  e.preventDefault();
  let text = "^" + e.target.value;
  setSearchText(text);
};

const handleChange = (e, dispatch) => {
  e.preventDefault();

  let sortingText = e.target.value;

  dispatch({
    type: "SORT_BY",
    sortText: sortingText,
  });
};

