import React from "react";
import * as headerController from "./headerController";
import { useStateValue } from "../../StateProvider";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom";
import * as cc from "../cart/cartController";

export const GetdropCartViews = () => {
  let cart = [];
  cart = cc.GetCart();
  let cartSubTotal = 0;
  cartSubTotal = cc.GetCartSubTotal(cart);
  let itemStatus = "";

  return (
    <>
      <div id="cartslider">
        <span class="items d-block text-dark text-capitalize">
          <em class="fw-bold text-dark text-capitalize">(3)</em> items
        </span>
        <ul id="slideWrap">
          <li>
            <div class="cart-image col">
              <img src="images/hp_product_img_m.jpg" alt="cart product image" />
            </div>
            <div class="cart-item col">
              <span class="cart-name d-block text-dark text-capitalize">
                bogssss
              </span>
              <span class="cart-desc d-block text-dark text-capitalize">
                woemn's crandall tall wool boot
              </span>
              <span class="cart-price d-block text-danger text-capitalize">
                $160.00
              </span>
              <span class="cart-quantity d-block text-dark text-capitalize">
                quantity: 1
              </span>
            </div>
          </li>
          <li>
            <div class="cart-image col">
              <img src="images/hp_product_img_m.jpg" alt="cart product image" />
            </div>
            <div class="cart-item col">
              <span class="cart-name d-block text-dark text-capitalize">
                bogsuuuuuu
              </span>
              <span class="cart-desc d-block text-dark text-capitalize">
                woemn's crandall tall wool boot
              </span>
              <span class="cart-price d-block text-danger text-capitalize">
                $160.00
              </span>
              <span class="cart-quantity d-block text-dark text-capitalize">
                quantity: 1
              </span>
            </div>
          </li>
          <li>
            <div class="cart-image col">
              <img src="images/hp_product_img_m.jpg" alt="cart product image" />
            </div>
            <div class="cart-item col">
              <span class="cart-name d-block text-dark text-capitalize">
                bogstttttttt
              </span>
              <span class="cart-desc d-block text-dark text-capitalize">
                woemn's crandall tall wool boot
              </span>
              <span class="cart-price d-block text-danger text-capitalize">
                $160.00
              </span>
              <span class="cart-quantity d-block text-dark text-capitalize">
                quantity: 1
              </span>
            </div>
          </li>
        </ul>
        <a id="prev" href="#">
          &#8810;
        </a>
        <a id="next" href="#">
          &#8811;
        </a>
        <div class="row no-gutters">
          <div class="col-12">
            <hr />
            <span class="subtotal-text d-inline-block text-dark text-capitalize">
              Subtotal
            </span>
            <span class="subtotal-amount float-right d-inline-block text-dark text-capitalize">
              $136.97
            </span>
            <hr />
          </div>
          <div class="col-12">
            <a
              href="#"
              class="checkout-cart bg-dark rounded-0 text-uppercase text-center w-100 mx-auto d-block p-2 text-light text-decoration-none"
            >
              checkout
            </a>
          </div>
        </div>
      </div>
    </>
  );
};

export const GetHeaderTopViews = (props) => {
  return (
    <div id="top" className="w-100 mx-auto">
      <div className="container">
        <div className="row">
          <div className="col-lg-12 col-md-12 col-sm-12 col-12 one  justify-content-center align-self-center text-right text-light text-capitalize pt-2">
            <p>{props.topMessage}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export const GetHeaderMiddleViews = (props) => {
  let cart = cc.GetCart();
  let history = useHistory();

  return (
    <>
      <div id="middle">
        <div className="container">
          <div className="row">
            <div className="col-lg-3 col-md-4 col-sm-12 col-12 logo justify-content-start align-self-center">
              <Link to="/">
                <img
                  src={`/${props.middle.sitelogo}`}
                  alt="site logo"
                  className="img-fluid w-100"
                />
              </Link>
            </div>
            <div className="col-lg-6 col-md-4 col-sm-8 col-7 search justify-content-center align-self-center text-center position-relative">
              <form className="position-relative">
                <input
                  type="text"
                  placeholder="search"
                  className="w-100 border-0 outline-none outine-0 text-capitalize py-2 pl-2"
                />
                <button className="bg-transparent p-0 outline-0 outline-none border-0 position-absolute top-50 right-0 pr-3">
                  <i className="fas fa-search text-light"></i>
                </button>
              </form>
            </div>
            <div className="col-lg-3 col-md-4 col-sm-4 col-5 profile justify-content-end align-self-center text-right text-light">
              <Link
                to="/"
                className="text-light text-decoration-none text-capitalize"
              >
                <i className="fas fa-user pr-2"></i>
                log in
              </Link>
              <Link
                to="/cart"
                className="text-light text-decoration-none text-capitalize"
                id="header-cart"
                data-toggle="collapse"
                data-target="#cartDrpDown"
                aria-controls="cartDrpDown"
                aria-expanded="false"
              >
                <i className="fas fa-shopping-cart pr-2"></i>cart
                <span id="basket-total-top" className="text-danger">
                  ({cart.length})
                </span>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export const GetHeaderNavViews = () => {
  const [{ basket, sFilters }, dispatch] = useStateValue();
  let history = useHistory();

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-dark static-top">
        <div className="container p-0">
          <button
            className="navbar-toggler"
            type="button"
            data-toggle="collapse"
            data-target="#navbarResponsive"
            aria-controls="navbarResponsive"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarResponsive">
            <ul className="navbar-nav ml-auto d-flex w-100 justify-content-between">
              {headerController.getTaxonomy().map((deptCat) => {
                return (
                  <li key={deptCat.id} className="nav-item active">
                    <i className="fas fa-angle-down down d-lg-none d-md-block d-sm-block"></i>
                    <Link
                      className="nav-link text-uppercase text-light "
                      to=""
                      data-url={deptCat.url}
                      type="button"
                      id="dropdownMenu2"
                      aria-expanded="false"
                      onClick={(e) => {
                        let dispatchedData = {};
                        dispatchedData = {
                          taxonomy: "dept",
                          redirectUrl: "dept_Url",
                          taxonomyValue: deptCat.category,
                          taxonomyUrl: deptCat.url,
                        };
                        headerController.addFilters(
                          e,
                          dispatchedData,
                          dispatch,
                          history
                        );
                      }}
                    >
                      {deptCat.category}
                    </Link>
                    {headerController.getTaxonomy(deptCat.category).length >
                      0 && (
                      <div
                        className="dropdown-menu position-absolute w-100 rounded-0"
                        aria-labelledby="dropdownMenu2"
                      >
                        <div className="container">
                          <div className="row">
                            {headerController
                              .getTaxonomy(deptCat.category)
                              .map((typCat) => {
                                return (
                                  <div
                                    key={typCat.id}
                                    className="col-lg-3 col-md-12 col-12 first m-2"
                                  >
                                    <Link
                                      key={typCat.id}
                                      to=""
                                      data-url={typCat.url}
                                      data-taxonomy={typCat.taxonomy}
                                      className="title d-block text-dark text-decoration-none text-capitalize  mb-1 border-bottom border-dark"
                                      onClick={(e) => {
                                        let dispatchedData = {};

                                        dispatchedData = {
                                          taxonomy: "typ",
                                          redirectUrl: "typ_Url",
                                          taxonomyValue: typCat.category,
                                          taxonomyUrl: typCat.url,
                                        };

                                        headerController.addFilters(
                                          e,
                                          dispatchedData,
                                          dispatch,
                                          history
                                        );
                                      }}
                                    >
                                      {typCat.category}
                                    </Link>
                                    <i
                                      key={typCat.id}
                                      className="fas fa-angle-down d-none"
                                      data-toggle="collapse"
                                      data-target="#accordone1"
                                      aria-expanded="false"
                                    ></i>
                                    <div
                                      key={typCat.id}
                                      id="accordone1"
                                      className="collapse show"
                                    >
                                      {headerController
                                        .getTaxonomy(
                                          deptCat.category,
                                          typCat.category
                                        )
                                        .map((subTyp_1cat) => {
                                          return (
                                            <Link
                                              key={typCat.id}
                                              to=""
                                              data-url={subTyp_1cat.url}
                                              data-taxonomy={
                                                subTyp_1cat.taxonomy
                                              }
                                              className="d-block text-dark text-decoration-none text-capitalize mb-1"
                                              onClick={(e) => {
                                                let dispatchedData = {};

                                                dispatchedData = {
                                                  taxonomy: "subTyp_1",
                                                  redirectUrl: "subTyp_1_Url",
                                                  taxonomyValue:
                                                    subTyp_1cat.category,
                                                  taxonomyUrl: subTyp_1cat.url,
                                                };

                                                headerController.addFilters(
                                                  e,
                                                  dispatchedData,
                                                  dispatch,
                                                  history
                                                );
                                              }}
                                            >
                                              {subTyp_1cat.category}
                                            </Link>
                                          );
                                        })}
                                    </div>
                                  </div>
                                );
                              })}
                          </div>
                        </div>
                      </div>
                    )}
                  </li>
                );
              })}
            </ul>
          </div>
        </div>
      </nav>
    </>
  );
};
function dropDownAction() {
  var x = document.getElementById("cartslider");
  if (x.style.display === "none") {
    x.style.display = "block";
  } else {
    x.style.display = "none";
  }
}
