import React from "react";
import * as headerController from "./headerController";
import { useStateValue } from "../../StateProvider";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom";
import * as cc from "../cart/cartController";

export const GetdropCartViews = () => {
  let cart = [];
  cart = cc.GetCart();
  let cartSubTotal = 0;
  cartSubTotal = cc.GetCartSubTotal(cart);
  let itemStatus = "";
  return (
    <>
      <div id="cartslider">
        <span class="items d-block text-dark text-capitalize">
          <em class="fw-bold text-dark text-capitalize">(3)</em> items
        </span>
        <ul id="slideWrap">
          <li>
            <div class="cart-image col">
              <img src="images/hp_product_img_m.jpg" alt="cart product image" />
            </div>
            <div class="cart-item col">
              <span class="cart-name d-block text-dark text-capitalize">
                bogs
              </span>
              <span class="cart-desc d-block text-dark text-capitalize">
                woemn's crandall tall wool boot
              </span>
              <span class="cart-price d-block text-danger text-capitalize">
                $160.00
              </span>
              <span class="cart-quantity d-block text-dark text-capitalize">
                quantity: 1
              </span>
            </div>
          </li>
        </ul>
        <a id="prev" href="#">
          &#8810;
        </a>
        <a id="next" href="#">
          &#8811;
        </a>
        <div class="row no-gutters">
          <div class="col-12">
            <hr />
            <span class="subtotal-text d-inline-block text-dark text-capitalize">
              Subtotal
            </span>
            <span class="subtotal-amount float-right d-inline-block text-dark text-capitalize">
              $136.97
            </span>
            <hr />
          </div>
          <div class="col-12">
            <a
              href="#"
              class="checkout-cart bg-dark rounded-0 text-uppercase text-center w-100 mx-auto d-block p-2 text-light text-decoration-none"
            >
              checkout
            </a>
          </div>
        </div>
      </div>
    </>
  );
};

export const GetHeaderTopViews = (props) => {
  return (
    <div id="top" className="w-100 mx-auto">
      <div className="container">
        <div className="row">
          <div className="col-lg-12 col-md-12 col-sm-12 col-12 one  justify-content-center align-self-center text-right text-light text-capitalize pt-2">
            <p>{props.topMessage}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export const GetHeaderMiddleViews = (props) => {
  let cart = cc.GetCart();
  let history = useHistory();

  return (
    <>
      <div id="middle">
        <div className="container">
          <div className="row">
            <div className="col-lg-3 col-md-4 col-sm-12 col-12 logo justify-content-start align-self-center">
              <Link to="/">
                <img
                  src={`/${props.middle.sitelogo}`}
                  alt="site logo"
                  className="img-fluid w-100"
                />
              </Link>
            </div>
