import React, { useState, useEffect } from 'react'
import { useStateValue } from '../StateProvider'
import { reactLocalStorage } from 'reactjs-localstorage'
import 'bootstrap/dist/css/bootstrap.min.css'
import * as sideNavViews from './sideNav/sideNavViews'
import * as categoryController from './category/categoryController'
import * as categoryView from './category/categoryViews'
import { useHistory } from 'react-router-dom'
import { useParams } from 'react-router'

export default function Category() {
  let url = ''
  const params = useParams()
  if (params) {
    url = params.url
  }

  let urlData = categoryController.getUrlTaxonomy(url)
  urlData.url = url
  let ppItems = 9
  let storedFilters = {}
  let filters = {}
  let history = useHistory()
  const [{basket,sFilters, products, sortby }, dispatch] = useStateValue()
console.log(basket)
  if (Object.keys(sFilters).length != 0) {
    reactLocalStorage.setObject('storedFilters', sFilters)
  }

  filters = reactLocalStorage.getObject('storedFilters')
  //reactLocalStorage.clear('storedFilters')
  filters[urlData.taxonomy] = urlData.category
  let temProducts = []
  let property = sortby

  temProducts = categoryController.getProducts(
    filters,
    products,
    dispatch,
    property,
    url,
    urlData.taxonomy
  )

  return (
    <React.Fragment>
      <section id='contentHolder'>
        <div id='category'>
          <div className='container'>
            <div className='row'>
              <BreadCrumbDisplay
                filters={filters}
                dispatch={dispatch}
                history={history}
                urlData={urlData}
              />
              {<SideBarDisplay
                filters={filters}
                products={temProducts}
                url={url}
              />}
              <CategoryProductArea
                dispatch={dispatch}
                filters={filters}
                products={temProducts}
                property={property}
              />
            </div>
          </div>
        </div>
      </section>
    </React.Fragment>
  )
}
const BreadCrumbDisplay = (props) => {
  let breadcrumbs = categoryController.getCategoryBreadCrumbs(props)
  return categoryView.BreadCrumbDisplay(breadcrumbs)
}
const SideBarDisplay = (props) => {
  let sideNavContent = sideNavViews.SideBarViews(props)
  return sideNavContent
}
const CategoryProductArea = (props) => {
  let products = []
  products = props.products
  //HOOK FOR PERPAGE PRODUCTS
  const [perpageProductscount, setPerpageProductscount] = useState(9)

  //HOOK FOR ACTIVE PAGE
  const [activePage, setCurrentPage] = useState(1)

  //GET CATEGORY TITLE
  let categoryTile = categoryController.getCategoryTitle(props)

  //PRODUCTS PER PAGE
  let productsPerPage = perpageProductscount

  //GET PRODUCTS ACCORDING TO PAGINATION
  let currentproducts = categoryController.pagination(
    products,
    productsPerPage,
    activePage
  ).currentproducts

  return (
    <>
      {categoryView.categotyProductsView(
        categoryTile,
        currentproducts,
        products,
        categoryController,
        setPerpageProductscount,
        props.dispatch,
        props.property
      )}
      {categoryView.paginationView(
        activePage,
        productsPerPage,
        products,
        categoryController,
        setCurrentPage
      )}
    </>
  )
}
