import axios from "axios";
import React, { useState, useEffect } from "react";
import { useStateValue } from "./StateProvider";

export const GetTaxonomyfromAPI = async () => {
  const [{ basket, allTaxonomy }, dispatch] = useStateValue();

  let url =
    "http://localhost:8500/celerant360/server/coldfusion/taxonomy.cfc?method=getTaxonomy";

  try {
    const response = await axios.get(url);
    if (!response.data.includes("table.cfdump_wddx")) {
      let data = response.data;
      dispatch({
        type: "ALL_TAXONOMY",
        aTaxonomy: [...data],
      });
    }
  } catch (error) {}
  console.log("alltaxonomy");

  console.log(allTaxonomy);
};
