import React, { Suspense } from "react";
import axios from "axios";
import { reactLocalStorage } from "reactjs-localstorage";
import * as API from "../API";

let texono = [];
export let taxonomy = [];
export let allProducts = [];

const resource = {
  user: [{ name: "a" }, { name: "b" }, { name: "c" }, { name: "d" }],
  posts: [
    { id: 1, text: "ss" },
    { id: 2, text: "nnn" },
    { id: 3, text: "ttt" },
    { id: 4, text: "uuu" },
  ],
};

export default function Test() {
  return (
    <Suspense fallback={<h1>Loading profile...</h1>}>
      <ProfileDetails />
      <Suspense fallback={<h1>Loading posts...</h1>}>
        <ProfileTimeline />
      </Suspense>
    </Suspense>
  );
}

function ProfileDetails() {
  // Try to read user info, although it might not have loaded yet
  return <h1>{resource.user.name}</h1>;
}

function ProfileTimeline() {
  let apiUrl =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getAllProducts&property=sortby&caturl=firearms&taxonomy=DEPT&category=Firearms";

  let data = [];
  axios.get(apiUrl).then((response) => {
    data = response.data;
    console.log(data);
    return (
      <ul>
        {data.map((post) => (
          <li key={post.STYLE_ID}>{post.STYLE_ID}</li>
        ))}
      </ul>
    );
  });
}
