import React, { useState, useEffect } from "react";
import { useStateValue } from "../StateProvider";
import { useHistory } from "react-router-dom";
import { reactLocalStorage } from "reactjs-localstorage";

function Cart() {
  let cartSessions = [];
  let cart = [];
  const [{ basket }, dispatch] = useStateValue();
  //reactLocalStorage.clear('cartSessions');
  let shopingCart = reactLocalStorage.getObject("cartSessions");

  return (
    <>
      <section id="contentHolder">
        <div id="viewcart" class="mb-4 pt-5">
          <div class="container">
            <div class="row">
              {CartTopLeft(cart)}
{CartTopRight(cart)}
              {cartContent(cart)}
             

              <section
                id="cart-end-information"
                class="col-lg-5 col-md-6 col-12 mt-3"
              >
                <button class="text-dark bg-light border-1 border-dark py-1 px-3 mb-3 rounded fw-bold d-block w-75">
                  Save for later
                </button>
                <button class="text-light bg-dark border-1 border-dark py-1 px-3 mb-3 rounded fw-bold d-block w-75">
                  Share cart link
                </button>
                <p class="mb-2">
                  <b>Have a coupon?</b> Apply your code in the Payment section
                  of checkout.
                </p>
                <p class="mb-2">
                  <b>Is this order a gift?</b>Select gift options in the
                  shipping section of checkout.{" "}
                  <a href="##" class="text-decoration-none">
                    Learn More
                  </a>
                </p>
              </section>
              <section
                id="cart-end-total"
                class="col-lg-6 col-md-6 col-12 mt-3 mx-auto"
              >
                <p class="w-100 fw-bold text-dark">
                  Subtotal
                  <span class="float-right fw-bold text-dark">$236.00</span>
                </p>
                <p class="w-100 text-secondary">
                  Standard Shipping
                  <span class="float-right text-secondary">FREE</span>
                </p>
                <p class="w-100 text-secondary">
                  Estimated taxes ?
                  <span class="float-right text-secondary">--</span>
                </p>
                <p class="w-100 text-secondary fs-5">
                  Order total
                  <span class="float-right text-secondary">$236.00</span>
                </p>
                <button class="w-100 rounded p-2 bg-dark fw-bold text-light text-center">
                  Proceed to checkout
                </button>
              </section>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

const CartTopLeft = (cart) => {
  return (
    <>
      <section
        id="cart-item-info"
        class="col-lg-6 col-md-6 col-sm-12 col-12 text-start"
      >
        <h3 class="text-dark">Shopping cart</h3>
        <p class="text-secondary">3 items</p>
        <button class="bg-secondary text-light p-2 border-1 fw-bold border-dark rounded">
          Continue Shopping
        </button>
      </section>
    </>
  );
};

const CartTopRight = (cart) => {

return(
<>
<section
                id="cart-item-total"
                class="col-lg-6 col-md-6 col-sm-12 col-12 text-right"
              >
                <h3 class="text-dark">$235.00</h3>
                <p class="text-secondary">Subtotal</p>
                <button class="bg-dark text-light border-1 p-2 fw-bold border-dark rounded">
                  Proceed to checkout
                </button>
              </section>
</>

)

}


const cartContent = (cart) => {

  return(
  <>

  </>
  )
}



export default Cart;
