const checkStockAvailability = product => {
  let stockInfo = {};

  if (product.stock > 0) {
    stockInfo.stock = product.stock;
    stockInfo.availability = 'In Stock';
  } else {
    stockInfo.stock = 0;
    stockInfo.availability = 'Out of Stock';
  }

  return stockInfo;
};

