import React, { Suspense, useState, useEffect } from "react";
import axios from "axios";
import { reactLocalStorage } from "reactjs-localstorage";
import * as API from "../API";
import { useStateValue } from "../StateProvider";
import * as cC from "./category/categoryController";
import { useParams } from "react-router";
let attempt = 0;
let texono = [];
export let taxonomy = [];
export let allProducts = [];

export default function Test() {
  document.getElementById("open").onclick = function openModal() {
    //adding class to the modal

    var element = document.getElementById("myModal");
    element.classList.add("show");
    element.classList.add("d-block");
    element.style.overflowX = "hidden";
    element.style.overflowY = "auto";
    var tag = document.createElement("div");
    tag.id = "overlay";
    tag.classList.add("modal-backdrop");
    tag.classList.add("show");
    tag.classList.add("fade");
    document.body.appendChild(tag);
  };
  document.getElementById("closeicon").onclick = function closeModal() {
    document.getElementById("myModal").classList.remove("show");
    document.getElementById("myModal").classList.remove("d-block");
    document.getElementById("overlay").classList.remove("show");
    document.getElementById("overlay").classList.remove("fade");
    var myobj = document.getElementById("overlay");
    myobj.remove();
  };
  document.getElementById("closebtn").onclick = function closeModal() {
    document.getElementById("myModal").classList.remove("show");
    document.getElementById("myModal").classList.remove("d-block");
    document.getElementById("overlay").classList.remove("show");
    document.getElementById("overlay").classList.remove("fade");
    var myobj = document.getElementById("overlay");
    myobj.remove();
  };
}
