import React, { useState, useEffect } from "react";
import { useStateValue } from "./StateProvider";

import axios from "axios";
import { reactLocalStorage } from "reactjs-localstorage";

export const GetTaxonomyfromAPI = async () => {
  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getTaxonomy";

  try {
    const response = await axios.get(url);
    if (!response.data.includes("table.cfdump_wddx")) {
      let data = response.data;
      reactLocalStorage.setObject("allTaxonomy", data);
    }
  } catch (error) {}
};

export const ApiProducts = async (
  property,
  catUrl,
  taxonomy,
  category,
  setAllProducts,
  setLoading,
  featured,
  ID
) => {
  let data = [];

  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getAllProducts&property=" +
    property +
    "&caturl=" +
    catUrl +
    "&taxonomy=" +
    taxonomy +
    "&category=" +
    category +
    "&ID=" +
    ID +
    "&featured=" +
    featured;

  try {
    const response = await axios.get(url);

    if (response.data.includes("table.cfdump_wddx")) {
      data = [];
    } else {
      data = response.data;
      if (Object.entries(data).length > 0) {
      } else {
        data = [];
      }
    }
  } catch (error) {
    data = [];
  }
  setAllProducts(data);
  setLoading(false);
  return "";
};

export const getProductByID = async (ID, setAllProducts, setLoading) => {
  let data = [];

  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getProductByID&ID=" +
    ID;

  try {
    const response = await axios.get(url);
    if (response.data.includes("table.cfdump_wddx")) {
      data = [];
    } else {
      data = response.data;
      if (Object.entries(data).length > 0) {
      } else {
        data = [];
      }
    }
  } catch (error) {
    data = [];
  }
  setAllProducts(data);
  setLoading(false);
  return "";
};

export const getWeRecommend = async (
  dept,
  brand,
  number,
  setAdditionalProducts,
  setLoading
) => {
  let data = [];

  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getWeRecommend&dept=" +
    dept +
    "&brand=" +
    brand +
    "&noOfItems=" +
    number;
  try {
    const response = await axios.get(url);
    if (response.data.includes("table.cfdump_wddx")) {
      data = [];
    } else {
      data = response.data;
      if (Object.entries(data).length > 0) {
      } else {
        data = [];
      }
    }
  } catch (error) {
    data = [];
  }
  setAdditionalProducts(data);
  setLoading(false);
  return "";
};

export const getProductSizes = async (ID, webStyle, siz, attr1, attr2) => {
  let data = [];

  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=getProductSizes&ID=" +
    ID +
    "&webStyle=" +
    webStyle +
    "&siz=" +
    siz +
    "&attr1=" +
    attr1 +
    "&attr2=" +
    attr2;
  try {
    const response = await axios.get(url);
    if (response.data.includes("table.cfdump_wddx")) {
      data = [];
    } else {
      data = response.data;
      if (Object.entries(data).length > 0) {
      } else {
        data = [];
      }
    }
  } catch (error) {
    data = [];
  }
  setAdditionalProducts(data);
  setLoading(false);
  return "";
};

export const setRecentsProducts = async (productID) => {
  let url =
    "http://localhost:8500/celerant360/server/CF/API.cfc?method=setRecent&ID=" +
    productID;

  try {
    const response = await axios.post(url);
  } catch (error) {}
  return "";
};
