import { prodImagesDir } from '../../settings'
import { Link } from 'react-router-dom';

export const displayFeaturedProducts = (products) => {
  return (
    <>
      <section id='productslider' className='mb-4'>
        <div className='container'>
          <div className='row'>
            <div className='col-12 heading text-center text-dark text-uppercase'>
              <p>featured items</p>
            </div>
            <div className='col-12'>
              <ul id='product' className='owl-carousel p-0 list-unstyled'>
         {products.map((product) => {
                  return (
                    <>
                      <li key={ product.style_id }>
                        <div className='productListing text-center'>
                          <Link
                            to={`/product/${product.style_id}/${product.url}`}
                            className='product text-decoration-none text-left'
                          >
                            <span className='image text-center mb-2 d-block'>
                              <img
                                src={prodImagesDir + product.smallImage}
                                alt={product.name}
                                className='img-fluid'
                              />
                            </span>
                            <span className='brand text-capitalize  text-dark d-block mb-1'>
                              {product.brand}{' '}
                            </span>
                            <span className='name text-dark d-block mb-1'>
                              {product.name}{' '}
                            </span>
                            <span className='pricing'>
                              <strong className='listPrice  text-dark d-block mb-1'>
                                ${product.regular_price}
                              </strong>
                              {product.sales_price > 0 && (
                                <strong className='salePrice  text-danger d-block mb-1'>
                                  ${product.sales_price}{' '}
                                </strong>
                              )}
                            </span>
                          </Link>
                        </div>
                      </li>
                    </>
                  )
                })}
              </ul>
            </div>
          </div>
        </div>
      </section>
    </>
  )
}
