import * as API from "./API";
import axios from "axios";
import { useStateValue } from "./StateProvider";
import { reactLocalStorage } from "reactjs-localstorage";
export const siteLogo = "images/logo.png";
export const headerTopMessage = "Content Goes Here If Any";
let texono = [];

export let taxonomy = [];
export let allProducts = [];

taxonomy = reactLocalStorage.getObject("allTaxonomy");
allProducts = reactLocalStorage.getObject("allProductsData");

if (Object.keys(taxonomy).length === 0) {
  alert("tttttt");
  const loadTaxonomy = async () => {
    await API.GetTaxonomyfromAPI();
  };
}
loadTaxonomy();
taxonomy = await reactLocalStorage.getObject("allTaxonomy");

loadTaxonomy();

const loadProducts = async () => {
  if (Object.keys(allProducts).length === 0) {
    alert("ppppppppp");

    await API.allProductsAPI();
  }
};

loadProducts();
allProducts = reactLocalStorage.getObject("allProductsData");

loadProducts();
