  }
  console.log(shopingCart);
  return shopingCart;
};

export const GetCartSubTotal = (cart) => {
  let subTotal = 0;

  if (cart && cart.length > 0) {
    cart.map((cartItem) => {
      subTotal += cartItem.total;
    });
  }

  return subTotal;
};
